var a = (document).ready(function(){
  a('.Carousel').carousel(
  {
    dist: 0,
    padding: 0,
    fullWidth: true,
    indicators: true,
    duration: 100,
  }
  );
});

autoplay()   
function autoplay() {
    a('.carousel').carousel('next');
    setTimeout(autoplay, 4500);
}
